void playAnimation()
{
    for (int i = 0; i < FRAME_COUNT; i++)
    {
        display.clearDisplay();
        display.drawBitmap(32, 0, frames[i], FRAME_WIDTH, FRAME_HEIGHT, 1);
        display.display();
        delay(FRAME_DELAY);
    }
}

